import React, { Component } from 'react';
import { Query, Mutation } from 'react-apollo';
import gql from 'graphql-tag';





class SignUpForm extends Component {

  constructor(props){
    super(props);

    this.state = {

    };

    //bindings
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleEmailChange = this.handleEmailChange.bind(this);
    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.handlePassword2Change = this.handlePassword2Change.bind(this);

  }


  handleSubmit() {
    //handle submit
  }
  handleNameChange(event) {
    this.setState({name: event.target.value});
  }
  handleEmailChange(event) {
    this.setState({email: event.target.value});
  }
  handlePasswordChange(event) {
    this.setState({password: event.target.value});
  }
  handlePassword2Change(event) {
    this.setState({password2: event.target.value});
  }

  render(){
    return(
      <body>
       <div className="container">
          <div className="col-md-6 mx-auto text-center">
             <div className="jumbotron">
                <h1 className="wv-heading--title">
                   Explore the world of memes
                </h1>
                <p className="wv-heading--subtitle">
                   Sign up today.
                </p>
             </div>
          </div>
          <div className="row">
             <div className="col-md-4 mx-auto">
                <div className="myform form ">
                   <form action="" method="post" name="login">
                      <div className="form-group">
                         <input type="text" name="name"  className="form-control my-input" id="name" placeholder="Name" />
                      </div>
                      <div className="form-group">
                         <input type="email" name="email"  className="form-control my-input" id="email" placeholder="Email" />
                      </div>
                      <div className="form-group">
                         <input type="number" min="0" name="phone" id="phone"  className="form-control my-input" placeholder="Phone" />
                      </div>
                      <div className="text-center ">
                         <button type="submit" className="btn btn-lg btn-primary btn-block text-uppercase">Sign Up</button>
                      </div>
                      <div className="col-md-12 ">
                         <div className="login-or">
                            <hr className="hr-or" />
                            <span className="span-or">or</span>
                         </div>
                      </div>
                      <div className="form-group">
                         <a className="btn btn-block g-button" href="#">
                         <i className="fa fa-google"></i> Sign up with Google
                         </a>
                      </div>
                      <p className="small mt-3">By signing up, you are indicating that you have read and agree to the <a href="#" className="ps-hero__content__link">Terms of Use</a> and <a href="#">Privacy Policy</a>.
                      </p>
                   </form>
                </div>
             </div>
          </div>
       </div>
    </body>
    );
  }



}

export default SignUpForm;
